package com.everlive.user.service;import org.hibernate.HibernateException;import org.springframework.beans.BeanUtils;import com.everlive.user.bo.User;import com.everlive.user.bo.UserForm;import com.everlive.user.dao.BaseDao;    public class UserManagerServiceImpl implements UserManagerService {        private BaseDao dao;        public void setDao(BaseDao dao) {          this.dao = dao;      }      @Override      public void regUser(UserForm userForm) throws HibernateException {          User user = new User();          BeanUtils.copyProperties(userForm, user);      }	@Override	public void out() {		System.out.println("xxx");			}	@Override	public void getStartList(UserForm userForm) {		dao.getStartList(userForm);	}	@Override	public void getSuspend1List(UserForm userForm) {		dao.getSuspend1List(userForm);	}	@Override	public void getSuspend2List(UserForm userForm) {		dao.getSuspend2List(userForm);	}	@Override	public void getSuspend3List(UserForm userForm) {		dao.getSuspend3List(userForm);			}  }  